import React         from 'react';
import PopupWithForm from './PopupWithForm';

function EditAvatarPopup(props) {

  const avatarRef                     = React.useRef();
  const [avatarLink,  setAvatarLink ] = React.useState('');
  const [avatarError, setAvatarError] = React.useState('');
  const [submitText,  setSubmitText ] = React.useState('Сохранить');

  React.useEffect(() => {
    if (props.isLoading) {
      setSubmitText('Сохранение...');
    } else {
      setSubmitText('Сохранить');
    }
  }, [props.isLoading]);

  React.useEffect(() => {
    if (avatarRef.current.value.length === 0) { 
      setAvatarError('Заполните это поле');
    } else if (validURL(avatarRef.current.value)) {
      setAvatarError('Введите URL');
    } else {
      setAvatarError('');
    }
  }, [avatarLink]);

  function validURL(str) {
    var pattern = new RegExp('^(https?:\\/\\/)?'+ // protocol
      '((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.)+[a-z]{2,}|'+ // domain name
      '((\\d{1,3}\\.){3}\\d{1,3}))'+ // OR ip (v4) address
      '(\\:\\d+)?(\\/[-a-z\\d%_.~+]*)*'+ // port and path
      '(\\?[;&a-z\\d%_.~+=-]*)?'+ // query string
      '(\\#[-a-z\\d_]*)?$','i'); // fragment locator
    return !!pattern.test(str);
  }

  function handleSubmit(evt) {
    evt.preventDefault();

    props.onUpdateAvatar({
      avatar: avatarRef.current.value
    })

    avatarRef.current.value = '';
  }

  function handleChangeAvatar(evt) {
    setAvatarLink(evt.target.value);
  }

  return (
    <PopupWithForm 
      name={'avatar'}
      title={'Обновить аватар'}
      submitText={submitText}
      isOpen={props.isOpen}
      onClose={props.onClose}
      onSubmit={handleSubmit}
    >
      <input type="url"
            className="popup__form-input popup__form-input_content_avatar"
            id="avatar"
            name="avatar-url"
            placeholder="Ссылка на аватар"
            value={avatarLink || ''}
            ref={avatarRef}
            onChange={handleChangeAvatar}
            required
      />
      <span className="popup__form-input-error popup__form-input-error_content_avatar">
        {avatarError}  
      </span>  
    </PopupWithForm>
  )
}

export default EditAvatarPopup;
