import React from 'react';

function PopupWithForm(props) {
  const [popupSelector, setOpenState] = React.useState(`popup popup_type_${props.name}`)
  if (!props.isOpen) {
    setOpenState(`popup popup_type_${props.name} popup_opened`)
  } else {
    setOpenState(`popup popup_type_${props.name}`)
  }
    return (
      <div className={`popup popup_type_${props.name} popup_opened`}>
        <div className={`popup__container popup__container_type_${props.name}`}>
          <button className="popup__close"
                  id={`close-${props.name}`}
                  type="button"
          >
          </button>
          <form className="popup__form"
                name={props.name}
                id={props.name}
                noValidate
          >
            <h2 className="popup__form-title">{props.title}</h2>
              {props.children}
            <button type="submit" className="popup__form-submit">{props.submitText}</button>
          </form>
        </div>
      </div>  
    )
}

export default PopupWithForm;
